    public Matematica() {
    }

    public float getCosseno(float angulo) {
        if (angulo < 0) {
            angulo = angulo + 2 * PI;
        }
        return cosseno[(int) (angulo / 0.017453292f)];
    }

    public float getSeno(float angulo) {
        if (angulo < 0) {
            angulo = angulo + 2 * PI;
        }
        return seno[(int) (angulo / 0.017453292f)];
    }

    public float[][] matrizTransposta(float[][] matriz) {
        float[][] transposta = new float[2][matriz.length];
        for (int i = 0; i < matriz.length; i++) {
            for (int j = 0; j < 2; j++) {
                transposta[j][i] = matriz[i][j];
            }
        }
        return transposta;
    }

    public float[][] multiplicaMatriz(float[][] matrizPerdaContraste, float[][] matrizTransposta) {
        int numSoma = matrizPerdaContraste.length;
        float[][] MR = new float[2][2];
        for (int i = 0; i < 2; i++) {
            for (int j = 0; j < 2; j++) {
                for (int s = 0; s < numSoma; s++) {
                    MR[i][j] += (matrizTransposta[i][s] * matrizPerdaContraste[s][j]);
                }
            }
        }
        return MR;
    }

    public float determinante(float[][] M) {
        return (M[0][0] * M[1][1] - M[0][1] * M[1][0]);
    }

    public float[] autovalor(float[][] m) {
        float a = 1;
        float b = ((m[0][0] * -1) + (m[1][1] * -1));
        float c = determinante(m);
        float delta = b * b - 4 * a * c;
        float lamb1 = (float) ((-1 * b) + Math.sqrt(delta)) / 2 * a;
        float lamb2 = (float) ((-1 * b) - Math.sqrt(delta)) / 2 * a;
        float[] r = new float[2];
        r[0] = lamb1;
        r[1] = lamb2;
        return r;
    }

    public float[][] autovetor(float[][] matriz, float[] autovalor) {
        float[][] r = new float[2][2];
        /*
         00  01
         10  11
         */
        float[][] m = new float[2][2];
        m[0][0] = matriz[0][0] - autovalor[0];
        m[1][1] = matriz[1][1] - autovalor[0];
        m[0][1] = matriz[0][1];
        m[1][0] = matriz[1][0];
        float y1 = 1;
        float x1 = ((m[1][1] * -1) * y1) / m[1][0];
        r[0][0] = x1;
        r[0][1] = y1;

        m[0][0] = matriz[0][0] - autovalor[1];
        m[1][1] = matriz[1][1] - autovalor[1];
        m[0][1] = matriz[0][1];
        m[1][0] = matriz[1][0];
        float y2 = 1;
        float x2 = ((m[1][1] * -1) * y2) / m[1][0];
        r[1][0] = x2;
        r[1][1] = y2;
        return r;
    }

    public int[] calcDistriGaussi(int numColuna, int numLinha, int largura, int altura) {
        int numCol = 0;
        int numLin = 0;
        int[] posicao = new int[2];
        Random rand = new Random();
        double conta = ((2 / 3.14) * (Math.sqrt(2 * Math.min(largura, altura))));
        try {
            for (;;) {
                numCol = (int) (rand.nextGaussian() * conta) + numColuna;
                numLin = (int) (rand.nextGaussian() * conta) + numLinha;

                if ((numCol >= 0 && numCol < largura) && (numLin >= 0 && numLin < altura)) {
                    break;
                }
            }
        } catch (Exception e) {
            System.out.println("erro gaussiana");
            System.exit(0);
        }
        posicao[0] = numLin;
        posicao[1] = numCol;
        return posicao;

    }
